[code]
#!/bin/bash
#filename=$1
i=0
fname=&ldquo;/etc/passwd&rdquo;
while&nbsp;read&nbsp;line
do
&nbsp;&nbsp;let&nbsp;&ldquo;i++&rdquo;
&nbsp;&nbsp;echo&nbsp;&rdquo;$i.&nbsp;&amp;nbsp;echo&amp;nbsp;&amp;nbsp;&quot;$line&quot;&amp;nbsp;'&amp;nbsp;cut&amp;nbsp;-d:&amp;nbsp;-f1&rdquo;
&nbsp;&nbsp;#echo&nbsp;&rdquo;$i&nbsp;$line&rdquo;
&nbsp;&nbsp;#array[$i]=$line
done&nbsp;&lt;$fname echo&nbsp;&rdquo;(*)&nbsp;Tong&nbsp;cong&nbsp;co&nbsp;$i&nbsp;user.&rdquo; exit&nbsp;0 [/code]
