(Có chỉnh sửa đôi chút để chạy được trên Linux ^^!)
#include &lt;stdio.h&gt; #define N 15 //So luong dong trong tam giac pascal void main() { int i, j, tgpascal[N], tmp[N]; tgpascal[0] = 1; for (i = 1; i &lt; N; i++) tgpascal[i] = 0; for (i = 0; i &lt; N; i++) { for (j = 0; j &lt;= i; j++) tmp[j] = tgpascal[j]; printf(&quot;n%5d&quot;, tmp[0]); for (j = 1; j &lt;= i; j++) { tmp[j] += tgpascal[j - 1]; printf(&quot;%5d&quot;, tmp[j]); } for (j = 0; j &lt;= i; j++) tgpascal[j] = tmp[j]; } printf(&quot;n&quot;); }

